public List<String> initFromList(List<String> keys, String extension) {
	LinkedList<String> wrong = new LinkedList<String>();
	for (String filename : keys) {
		File file = new File(filename);
		if (file.canRead()) {
			String basename = file.getName();
			int lastpos = basename.lastIndexOf(extension);
			if (lastpos <= 0) {
				wrong.add(filename);
				continue;
			}
			String firstname = basename.substring(0, lastpos - 1);
			int len = (int) file.length();
			byte[] key = new byte[len];
			FileInputStream inputStream = null;
			try {
				inputStream = new FileInputStream(file);
			} catch (FileNotFoundException e) {
				// should not be
				wrong.add(filename);
				continue;
			}
			int read = 0;
			int offset = 0;
			while (read > 0) {
				try {
					read = inputStream.read(key, offset, len);
				} catch (IOException e) {
					wrong.add(filename);
					read = -2;
					break;
				}
				offset += read;
				if (offset < len) {
					len -= read;
				} else {
					break;
				}
			}
			try {
				inputStream.close();
			} catch (IOException e) {
			}
			if (read < -1) {
				// wrong
				continue;
			}

